import React from 'react';
import { Box, Grid, Typography, Avatar, Link } from '@mui/material';

const AboutPage = () => {
  // 示例数据，可以根据实际情况替换
  const profiles = [
    {
      name: "Alice",
      avatar: "/path/to/alice.jpg", // 头像图片路径
      github: "https://github.com/alice",
      email: "alice@example.com",
      bio: "Experienced software developer with a passion for building web applications."
    },
    {
      name: "Bob",
      avatar: "/path/to/bob.jpg",
      github: "https://github.com/bob",
      email: "bob@example.com",
      bio: "Dedicated IT professional specializing in network security and cloud computing."
    },
    {
      name: "Carol",
      avatar: "/path/to/carol.jpg",
      github: "https://github.com/carol",
      email: "carol@example.com",
      bio: "Creative graphic designer with extensive experience in multimedia and print design."
    }
  ];

  return (
    <Box sx={{ flexGrow: 1, padding: 4 }}>
      <Grid container spacing={2} justifyContent="center">
        {profiles.map(profile => (
          <Grid item xs={12} sm={4} key={profile.name}>
            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <Avatar
                alt={profile.name}
                src={profile.avatar}
                sx={{ width: 100, height: 100, mb: 2 }}
              />
              <Typography variant="h6" color="text.primary" gutterBottom>
                <Link href={profile.github} color="inherit" target="_blank">
                  GitHub
                </Link>
                <br/>
                <Link href={`mailto:${profile.email}`} color="inherit">
                  Email
                </Link>
              </Typography>
              <Typography variant="body1" color="text.secondary">
                {profile.bio}
              </Typography>
            </Box>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default AboutPage;
