import React, { useEffect } from 'react';
import { Waline } from '@waline/client';

export default function PostPage({ post }) {
  useEffect(() => {
    Waline({
      el: '#waline',
      serverURL: '你的Waline服务器地址',
      // 其他配置...
    });
  }, []);

  return (
    <div>
      <h1>{post.title}</h1>
      {/* 文章内容 */}
      <div id="waline"></div>
    </div>
  );
}
