import React from "react";
import {
  Drawer,
  Card,
  IconButton,
  CardContent,
  Typography,
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";

// 假设这些是你的组件状态和处理函数
const isFilterDrawerOpen = true; // 示例状态
const handleFilterClose = () => {}; // 示例处理函数

const FilterDrawer = () => {
  return (
    <Drawer
      anchor="left"
      open={isFilterDrawerOpen}
      onClose={handleFilterClose}
      sx={{ "& .MuiDrawer-paper": { height: "calc(100% - 64px)", top: 64 } }} // 适应父元素高度的样式
      ModalProps={{
        keepMounted: true, // 更好的性能
        BackdropProps: { invisible: true }, // 禁用背景遮罩
      }}
    >
      <Card sx={{ width: 250 }}>
        <IconButton
          onClick={handleFilterClose}
          sx={{ position: "absolute", right: 8, top: 8 }}
        >
          <CloseIcon />
        </IconButton>
        <CardContent>
          <Typography variant="h6">Filter Configuration</Typography>
          {/* Filter配置内容 */}
        </CardContent>
      </Card>
    </Drawer>
  );
};

export default FilterDrawer;
