+            ...node,
+            value: node.latestCollaboration, // 设置 value 以供 visualMap 使用
+          };
+        }
+        return node;
+      });
+
+      const initialOption = {
+        title: {
+          text: "科研合作网络图",
+          top: "bottom",
+          left: "right",
