+  // 主节点
+  nodes.push({
+    name: "Main Node",
+    category: 0,
+    symbolSize: 70,
+    itemStyle: { color: "red" },
+  });
+
+  // 其他节点
+  for (let i = 1; i <= nodesCount; i++) {
+    nodes.push({
+      name: `Node ${i}`,
+      category: 1,
+      symbolSize: 20 + Math.random() * 40, // 20 ~ 60 的大小
+      itemStyle: { color: "grey" },
+    });
+    links.push({
+      source: "Main Node",
+      target: `Node ${i}`,
+    });
+  }
+
+  return { nodes, links };
+};
