+  name: string;
+  connections: {
+    advisor_id: number;
+    relation: Array<{
+      class: string;
+      role: string;
+      duration: {
+        start: { year: number; month: number };
+        end: { year: number; month: number };
+      };
+    }>;
+    collaborations: Array<{
+      papername: string;
+      year: number;
+      url: string;
+    }>;
+    latestCollaboration: number;
+    relationFactor: number;
+  }[];
