+            <CloseIcon />
+          </IconButton>
+          <CardContent>
+            {/* Filter配置内容 */}
+            <Typography variant="h6">Filter Configuration</Typography>
+          </CardContent>
+        </Card>
+      </Drawer>
+    </Toolbar>
+  );
+};
