import React, { useState } from "react";
import {
  Toolbar,
  Button,
  Typography,
  Box,
  Menu,
  MenuItem,
  Drawer,
  Card,
  IconButton,
  CardContent,
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";

const RenderTabs = ({ onFilter, onListView, onGraphMode }) => {
  const [graphModeAnchorEl, setGraphModeAnchorEl] = useState(null);
  const [isFilterDrawerOpen, setIsFilterDrawerOpen] = useState(false);

  const handleGraphModeClick = (event) => {
    setGraphModeAnchorEl(event.currentTarget);
  };

  const handleGraphModeClose = () => {
    setGraphModeAnchorEl(null);
  };

  const handleFilterClick = () => {
    setIsFilterDrawerOpen(true);
  };

  const handleFilterClose = () => {
    setIsFilterDrawerOpen(false);
  };

  return (
    <Toolbar
      sx={{
        justifyContent: "space-between",
        backgroundColor: "white",
        boxShadow: "0px 2px 3px -2px rgba(0,0,0,0.12)",
      }}
    >
      <Typography variant="h6">Render Tab</Typography>
      <Box sx={{ display: "flex", gap: 2 }}>
        <Button
          onClick={handleFilterClick}
          color="inherit"
          sx={{ textTransform: "none" }}
        >
          Filter
        </Button>
        <Button color="inherit" sx={{ textTransform: "none" }}>
          List View
        </Button>
        <Button
          onClick={handleGraphModeClick}
          color="inherit"
          sx={{ textTransform: "none" }}
        >
          Graph Mode
        </Button>
        <Menu
          anchorEl={graphModeAnchorEl}
          open={Boolean(graphModeAnchorEl)}
          onClose={handleGraphModeClose}
        >
          <MenuItem onClick={handleGraphModeClose}>Focused</MenuItem>
          <MenuItem onClick={handleGraphModeClose}>Diverged</MenuItem>
        </Menu>
      </Box>
    </Toolbar>
  );
};

export default RenderTabs;
