import React from "react";
import TopMenu from "@/components/topTab";
import MainContent from "@/components/mainPage/mainContent";

interface MainProps {
  params?: {
    id?: string;
  };
}

export default function Page({ params }: MainProps) {
  // 使用可选链和空值合并运算符确保id有一个默认值
  const id = parseInt(params?.id ?? "0"); // 如果params或params.id不存在，则默认为"0"

  return (
    <div>
      <TopMenu />
      <MainContent id={id} />
    </div>
  );
}
