+.container {
+    width: 100%;
+    max-width: 1200px;
+    margin: auto;
+    padding: 20px;
+  }
+  
+  .introSection, .commentsSection {
+    margin-bottom: 20px;
+  }
+  
+  .imageGallery {
+    margin: 20px 0;
+    overflow: hidden;
+  }
+  
+  .galleryGrid {
+    display: flex;
+    margin-left: -30px; /* gutter size offset */
+    width: auto;
+  }
+  
+  .myMasonryGridColumn {
+    padding-left: 30px; /* gutter size */
+    background-clip: padding-box;
+  }
+  
+  @keyframes scroll {
+    0% { transform: translateY(0); }
+    100% { transform: translateY(-100%); }
+  }
+  
+  .myMasonryGridColumn img {
+    animation: scroll 60s linear infinite;
+    animation-play-state: running;
+  }
+  
+  .myMasonryGridColumn img:hover {
+    animation-play-state: paused;
+  }
\ No newline at end of file

commits.content:
.container {
  background-color: #fff;
  text-align: center;
  padding-bottom: 2rem;
}

.introSection h1 {
  margin-top: 2rem;
  font-size: 2.5rem;
}

.introSection,
.imageGallery,
.commentsSection {
  margin: 2rem 0;
}

.galleryGrid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 1rem;
  justify-content: center;
  padding: 1rem;
}

.commentsList p {
  margin: 0.5rem 0;
}

/* 调整媒体查询以适应不同屏幕尺寸 */
@media (max-width: 768px) {
  .introSection h1 {
    font-size: 2rem;
  }
}
