commits.content:
.container {
  background-color: #fff;
  text-align: center;
  padding-bottom: 2rem;
}

.introSection h1 {
  margin-top: 2rem;
  font-size: 2.5rem;
}

.introSection,
.imageGallery,
.commentsSection {
  margin: 2rem 0;
}

.galleryGrid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 1rem;
  justify-content: center;
  padding: 1rem;
}

.commentsList p {
  margin: 0.5rem 0;
}

/* 调整媒体查询以适应不同屏幕尺寸 */
@media (max-width: 768px) {
  .introSection h1 {
    font-size: 2rem;
  }
}
